// staff Routes

router.post('/auth/signup', createStaff);
router.post('/auth/signin', signinStaff);
router.get('/search', searchStaffFirstnameOrLastname);
router.post('/edit', editStaff);
router.post('/delete', deleteStaff);

export default router;

