      return res.status(status.success).send(successMessage.data);
    }
    catch (error) {
      errorMessage.error = 'Operation was not successful';
      return res.status(status.error).send(errorMessage.error);

    }
  };
  
  export {
    createStaff,
    signinStaff,
    searchStaffFirstnameOrLastname,
  };

